#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin>>t;    while(t--){      int n;      cin>>n;      vector<int> a(n);      int neg=0;      for(int i=0;i<n;i++){        cin>>a[i];        if(a[i]==-1)neg++;      }      int sum=n-neg*2;      int op=0;       while(sum<0){          sum=sum+2;          op++;          neg--;        }      if(neg%2==0){        cout<<op<<endl;      }else{        cout<<op+1<<endl;      }       }     return 0;}