#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<long long >a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        long long mini=LONG_LONG_MAX;        for(int i=1;i<n;i++){            mini=min(mini,a[i]-a[i-1]);        }        if(mini<0){            cout<<0<<endl;        }else{        long long op=(mini/2)+1;        cout<<op<<endl;}    }      return 0;}