#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n),b,c;        for(int i=0;i<n;i++)cin>>a[i];                int maxi=*max_element(a.begin(),a.end());        for(int i=0;i<n;i++){            if(maxi==a[i]){                c.push_back(a[i]);            }            else{                b.push_back(a[i]);            }        }        int lb=b.size();        int lc=c.size();        if(lb==0||lc==0){            cout<<-1<<endl;        }else{            cout<<lb<<" "<<lc<<endl;            for(int i=0;i<lb;i++) cout<<b[i]<<" ";            cout<<endl;            for(int i=0;i<lc;i++) cout<<c[i]<<" ";            cout<<endl;        }       }          return 0;}