#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;     while(t--){        int n;        cin>>n;        vector<long long>b(n);        for(int i=0;i<n;i++){            cin>>b[i];        }        vector<long long> a;        a.push_back(b[0]);                for(int i=1;i<n;i++){            a.push_back(b[i]);            if(b[i]<b[i-1]){                a.push_back(b[i]);            }        }        int m=a.size();        cout<<m<<endl;        for(int i=0;i<m;i++)cout<<a[i]<<" ";        cout<<endl;               }     return 0;}