#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;         vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];            a[i]--; // make values 0-based        }         // Build graph        vector<vector<int>> graph(n);         // Original order adjacency        for (int i = 0; i + 1 < n; i++) {            graph[i].push_back(i + 1);            graph[i + 1].push_back(i);        }         // Sorted order adjacency        vector<int> pos(n);        for (int i = 0; i < n; i++) {            pos[a[i]] = i;        }        for (int v = 0; v + 1 < n; v++) {            int u1 = pos[v];            int u2 = pos[v + 1];            graph[u1].push_back(u2);            graph[u2].push_back(u1);        }         // Bipartite check        vector<int> color(n, -1);        bool ok = true;         for (int i = 0; i < n && ok; i++) {            if (color[i] == -1) {                queue<int> q;                q.push(i);                color[i] = 0;                 while (!q.empty() && ok) {                    int u = q.front();                    q.pop();                    for (int v : graph[u]) {                        if (color[v] == -1) {                            color[v] = color[u] ^ 1;                            q.push(v);                        } else if (color[v] == color[u]) {                            ok = false;                            break;                        }                    }                }            }        }         cout << (ok ? "YES\n" : "NO\n");    }     return 0;}