#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin>>t;    while(t--){        long long n;        cin>>n;        vector<long long>a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        int coun0=0;        int coun1=0;        for(int i=0;i<n;i++){            if(a[i]==0){                coun0+=1;            }            if(a[i]==1){                coun1+=1;            }        }        if(coun0==1&&coun1==0){             cout<<"YES \n";}        else if(coun0>0&&coun1>0){            cout<< "YES \n";        }        else{            cout<<"NO \n";        }                     }     return 0;}